import React, { useState, useEffect } from 'react';
import './App.css';
import ExpenseList from './components/ExpenseList'
import ExpenseForm from './components/ExpenseForm'
import Alert from './components/Alert'








function App() {

// ********* fetch functions ********** //
  function fetchExpenses() {
    fetch('http://localhost:3000/expenses')
    .then(r => r.json())
    .then(data => setExpenses(data))
  };

  function postExpense(expense) {
    fetch('http://localhost:3000/expenses', {
      method: 'POST',
      headers: {
        "Content-Type": "application/json" 
      },
      body: JSON.stringify(expense)
    })
    .then(r => r.json())
    .then(newExpense => {
      setExpenses([...expenses, newExpense])
      setAmount('')
      setCharge('')
    })


  }

  function deleteExpense(expense) {
    fetch('http://localhost:3000/expenses', {
      method: 'DELETE'
    })
    .then(r)
  }

  useEffect(() => {

    fetchExpenses();
  }, [])

  //************** state values  **************/
  // all expenses, add expense
  const [expenses, setExpenses] = useState([])
  // single expense
  const [charge, setCharge] = useState('')
  // single amount
  const [amount, setAmount] = useState('')
  // alert
  // const[alert, showAlert] = useState({show: false})

  //************** functionality  **************/
  const handleCharge = e => {
    setCharge(e.target.value)
  }

  const handleAmount = e => {
    setAmount(e.target.value)
  }

  const handleSubmit = e => {
    e.preventDefault();
    if (charge !== '' && amount > 0) {
      const singleExpense = {charge, amount}
      postExpense(singleExpense)
      console.log(singleExpense)
    }
    else {
      //handle calling an Alert
    }
  }



  return (

    <>
      {/* {alert.show && <Alert type={alert.type} text={alert.text}/>} */}
      <h1>Budget Calculator</h1>
      <main className='App'>
        <ExpenseForm charge={charge} amount={amount} handleCharge={handleCharge} handleAmount={handleAmount} handleSubmit={handleSubmit}/>
        <ExpenseList expenses={expenses}/>
      </main>
      <h1>
        Total Spending: <span className="total">
          ${expenses.reduce((acc, curr) =>{
            return acc += parseInt(curr.amount)
          },0)}
        </span>
      </h1>

    </>
  );
}

export default App;
